document.getElementById('search-input').addEventListener('input', function() {
  const searchTerm = this.value.toLowerCase();
  const content = document.getElementById('book-content').innerText;
  const regex = new RegExp(searchTerm, 'gi');
  const highlightedContent = content.replace(regex, match => `<span class="highlight">${match}</span>`);
  document.getElementById('book-content').innerHTML = highlightedContent;

  // Rolar até a primeira ocorrência do termo pesquisado
  if (searchTerm) {
    const firstMatch = document.querySelector('.highlight');
    if (firstMatch) {
      firstMatch.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  }
});
